import { createLocal } from 'moment/src/lib/create/local';
import { createUTC } from 'moment/src/lib/create/utc';
import { createInvalid } from 'moment/src/lib/create/valid';
import { isMoment } from 'moment/src/lib/moment/constructor';
import { min, max } from 'moment/src/lib/moment/min-max';
import { now } from 'moment/src/lib/moment/now';
import momentPrototype from 'moment/src/lib/moment/prototype';

function createUnix (input) {
    return createLocal(input * 1000);
}

function createInZone () {
    return createLocal.apply(null, arguments).parseZone();
}

export {
    now,
    min,
    max,
    isMoment,
    createUTC,
    createUnix,
    createLocal,
    createInZone,
    createInvalid,
    momentPrototype
};
