import { makeGetSet } from 'moment/src/lib/moment/get-set';
import { addFormatToken } from 'moment/src/lib/format/format';
import { addUnitAlias } from 'moment/src/lib/units/aliases';
import { addUnitPriority } from 'moment/src/lib/units/priorities';
import { addRegexToken, match1to2, match2 } from 'moment/src/lib/parse/regex';
import { addParseToken } from 'moment/src/lib/parse/token';
import { DATE } from 'moment/src/lib/units/constants';
import toInt from 'moment/src/lib/utils/to-int';

// FORMATTING

addFormatToken('D', ['DD', 2], 'Do', 'date');

// ALIASES

addUnitAlias('date', 'D');

// PRIORITY
addUnitPriority('date', 9);

// PARSING

addRegexToken('D',  match1to2);
addRegexToken('DD', match1to2, match2);
addRegexToken('Do', function (isStrict, locale) {
    // TODO: Remove "ordinalParse" fallback in next major release.
    return isStrict ?
      (locale._dayOfMonthOrdinalParse || locale._ordinalParse) :
      locale._dayOfMonthOrdinalParseLenient;
});

addParseToken(['D', 'DD'], DATE);
addParseToken('Do', function (input, array) {
    array[DATE] = toInt(input.match(match1to2)[0]);
});

// MOMENTS

export var getSetDayOfMonth = makeGetSet('Date', true);
