import { addFormatToken } from 'moment/src/lib/format/format';
import { addUnitAlias } from 'moment/src/lib/units/aliases';
import { addUnitPriority } from 'moment/src/lib/units/priorities';
import { addRegexToken, match3, match1to3 } from 'moment/src/lib/parse/regex';
import { daysInYear } from 'moment/src/lib/units/year';
import { createUTCDate } from 'moment/src/lib/create/date-from-array';
import { addParseToken } from 'moment/src/lib/parse/token';
import toInt from 'moment/src/lib/utils/to-int';

// FORMATTING

addFormatToken('DDD', ['DDDD', 3], 'DDDo', 'dayOfYear');

// ALIASES

addUnitAlias('dayOfYear', 'DDD');

// PRIORITY
addUnitPriority('dayOfYear', 4);

// PARSING

addRegexToken('DDD',  match1to3);
addRegexToken('DDDD', match3);
addParseToken(['DDD', 'DDDD'], function (input, array, config) {
    config._dayOfYear = toInt(input);
});

// HELPERS

// MOMENTS

export function getSetDayOfYear (input) {
    var dayOfYear = Math.round((this.clone().startOf('day') - this.clone().startOf('year')) / 864e5) + 1;
    return input == null ? dayOfYear : this.add((input - dayOfYear), 'd');
}
